import React, { Component } from "react";
import Input from "@material-ui/core/Input";
import axios from "axios";
import { Button, Fab } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";

class Scrap extends Component {
  state = {
    url: ""
  };
  handleClick() {}
  handlechange = e => {
    this.setState({ url: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const Url = "http://localhost:8080/scraper";
    const config = {
      url: this.state.url
    };

    axios
      .post(Url, config)
      .then(res => {
        if (res.status === 200) {
          alert(`Website ${this.state.url} scraped !`);
          this.setState({ url: "" });
        }
      })
      .catch(err => {
        return alert(err);
      });
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Input
            name="url"
            type="text"
            placeholder="Type here Website Url"
            onChange={this.handlechange}
            value={this.state.url}
            style={{ width: "30em", margin: "3%" }}
          />
          <div className="form-data">
            <Button type="submit"> Send</Button>
          </div>
        </form>{" "}
        <Fab color aria-label="Add" onClick={this.handleClick}>
          <AddIcon />
        </Fab>
      </div>
    );
  }
}

export default Scrap;
