import React, { Component } from "react";
import Input from "@material-ui/core/Input";
import axios from "axios";
import { Button, Fab } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import Grid from "@material-ui/core/Grid";

class Scrap extends Component {
  state = {
    url: "",
    active: true,
    path: ""
  };

  handleClick = e => {
    this.setState({ active: !this.state.active });
  };

  handlechange = e => {
    this.setState({ [e.target.value]: e.target.value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const Url = "http://localhost:8080/scraper";
    const config = {
      url: this.state.url
    };

    axios
      .post(Url, config)
      .then(res => {
        if (res.status === 200) {
          alert(`Website ${this.state.url} scraped !`);
          this.setState({ url: "" });
        }
      })
      .catch(err => {
        return alert(err);
      });
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Input
            name="path"
            type="text"
            placeholder="Type path"
            onChange={this.handlechange}
            value={this.state.path}
            style={{ width: "30em", margin: "3%" }}
          />
          <Input
            name="url"
            type="text"
            placeholder="Type here Website Url"
            onChange={this.handlechange}
            value={this.state.url}
            style={{ width: "30em", margin: "3%" }}
          />

          <Button type="submit" style={{ margin: "1%" }}>
            {" "}
            Send
          </Button>
        </form>
        <div>
          <p>Read before starting :</p>
        </div>
        <div className={this.state.active ? "Selected" : "NotSelected"}>
          <Grid container justify="center">
            <Grid item xs={12} sm={4}>
              <p>
                Don't forget to put 'http://' or 'https://' in your Url, when
                clicking on Send button a folder 'Download' will be creating at
                the root of your OS ! Enjoy !
              </p>
            </Grid>
          </Grid>
        </div>
        <Fab color aria-label="Add" onClick={this.handleClick}>
          <AddIcon />
        </Fab>
      </div>
    );
  }
}

export default Scrap;
